import {useEffect, useState} from "react";
import {Link, NavLink} from "react-router-dom";

function StudentListFunc({students}) {
    const [student, setStudents] = useState(students);


    const [name, setName] = useState("");

    useEffect(() => {
    //     Call API search name
        const temp = students.filter(temp => temp.name.includes(name))
        setStudents(temp)
    }, [name])

    useEffect(() => {
        return () => {
            //clean up <=> componentWillUnmount
        }
    }, [])

    return (
        <>
            <input value={name} onChange={(e) => setName(e.target.value)}/>
            <table className="table table-hover">
                <thead>
                <tr>
                    <th>STT</th>
                    <th>Name</th>
                    <th>Address</th>
                    <th>Point</th>
                    <th>Action</th>
                </tr>
                </thead>
                <tbody>
                {
                    student.map((item, index) =>
                        <tr key={item.id}>
                            <td>{index}</td>
                            <td>{item.name}</td>
                            <td>{item.address}</td>
                            <td>{item.point}</td>
                            <td></td>
                        </tr>
                    )
                }
                </tbody>
            </table>
        </>
    )
}

export default StudentListFunc;
