import './App.css';
import React, {useState} from "react"
import StudentListFunc from "./component/student/StudentListFunc";
import StudentCreate from "./component/student/StudentCreate";
import {ToastContainer} from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import {BrowserRouter, NavLink, Route, Routes} from "react-router-dom";


function App() {
    const [students, setStudents] = useState([
        { id: 1, name: "HaiTT1", address: "Quảng Nam", point: 9 },
        { id: 2, name: "HaiTT2", address: "Quảng Nam", point: 9 },
        { id: 3, name: "HaiTT3", address: "Quảng Nam", point: 9 }
    ]);
    const addStudent = (newStudent) => {
        setStudents([...students, newStudent]);
    };
    return (
        <>
            <BrowserRouter>
                <div className="navbar navbar-expand-lg navbar-light bg-light">
                    <NavLink className="navbar-brand" to="/student">Danh sách</NavLink>
                    <NavLink className="navbar-brand" to="/create">Thêm mới</NavLink>
                </div>
                <Routes>
                    <Route path="/create" element={<StudentCreate onAddStudent={addStudent} />} />
                    <Route path="/student" element={<StudentListFunc students={students} setStudents={setStudents} />} />
                </Routes>
            </BrowserRouter>
            <ToastContainer />
        </>
    );
}

export default App;
