import {useEffect, useState} from "react";

function StudentListFunc({students}) {
    const [student, setStudents] = useState(students);


    const [name, setName] = useState("");

    useEffect(() => {
    //     Call API search name
        const temp = students.filter(temp => temp.name.includes(name))
        setStudents(temp)
    }, [name])

    useEffect(() => {
        return () => {
            //clean up <=> componentWillUnmount
        }
    }, [])

    return (
        <>
            <input value={name} onChange={(e) => setName(e.target.value)}/>
            <table className="table table-hover">
                <thead>
                <tr>
                    <th>STT</th>
                    <th>Name</th>
                    <th>Address</th>
                    <th>Point</th>
                    <th>Action</th>
                </tr>
                </thead>
                <tbody>
                {
                    student.map((item, index) =>
                        <tr key={item.id}>
                            <td>{index}</td>
                            <td>{item.name}</td>
                            <td>{item.address}</td>
                            <td>{item.point}</td>
                            <td>
                                <button type="button" className="btn btn-danger" data-bs-toggle="modal"
                                        data-bs-target="#exampleModal">
                                    Delete
                                </button>
                                {selectedStudent && selectedStudent.id === item.id && (
                                    <div className="modal fade show" id={`deleteModal${item.id}`} tabIndex="-1" aria-labelledby="deleteModalLabel"
                                         style={{ display: 'block' }} aria-hidden="true">
                                        <div className="modal-dialog">
                                            <div className="modal-content">
                                                <div className="modal-header">
                                                    <h5 className="modal-title" id="deleteModalLabel">Bạn có chắc muốn xoá sinh viên: {item.name}?</h5>
                                                    <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"
                                                            onClick={() => setSelectedStudent(null)}></button>
                                                </div>
                                                <div className="modal-footer">
                                                    <button type="button" className="btn btn-secondary" data-bs-dismiss="modal" onClick={() => setSelectedStudent(null)}>Close</button>
                                                    <button type="button" className="btn btn-danger" onClick={() => handleDelete(item)}>Delete</button>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                )}
                            </td>
                        </tr>
                    )
                }
                </tbody>
            </table>
        </>
    )
}

export default StudentListFunc;
