import {useEffect, useState} from "react";
import {toast} from "react-toastify";

function StudentListFunc({students,setStudents}) {

    const [name, setName] = useState("");

    const [selectedStudent, setSelectedStudent] = useState(null);


    // useEffect(() => {
    //     //     Call API search name
    //     const temp = students.filter(temp => temp.name.includes(name))
    //
    //         setStudents(temp)
    //
    //
    // }, [name])

    // useEffect(() => {
    //     return () => {
    //         //clean up <=> componentWillUnmount
    //     }
    // }, [])
    const handleDelete = (id) => {



        const updatedStudents = students.filter(item => item.id !== id);
        setStudents(updatedStudents);
        toast.success("Xoá thành công ")
        setSelectedStudent(null);
    };
    return (
        <>
            <input value={name} onChange={(e) => setName(e.target.value)}/>
            <table className="table table-hover">
                <thead>
                <tr>
                    <th>STT</th>
                    <th>Name</th>
                    <th>Address</th>
                    <th>Point</th>
                    <th>Action</th>
                </tr>
                </thead>
                <tbody>
                {
                    students
                        .filter(item => item.name.includes(name))
                        .map((item, index) =>
                        <tr key={item.id}>
                            <td>{index+1}</td>
                            <td>{item.name}</td>
                            <td>{item.address}</td>
                            <td>{item.point}</td>
                            <td>
                                <button type="button" className="btn btn-danger" data-bs-toggle="modal"
                                        data-bs-target={`#deleteModal${item.id}`} onClick={() => setSelectedStudent(item)}>
                                    Delete
                                </button>
                                {selectedStudent && selectedStudent.id === item.id && (
                                    <div className="modal fade show" id={`deleteModal${item.id}`} tabIndex="-1"
                                         aria-labelledby="deleteModalLabel"
                                         style={{display: 'block'}} aria-hidden="false">
                                        <div className="modal-dialog">
                                            <div className="modal-content">
                                                <div className="modal-header">
                                                    <h5 className="modal-title" id="deleteModalLabel">Bạn có chắc muốn
                                                        xoá sinh viên: {item.name}?</h5>
                                                    <button type="button" className="btn-close" data-bs-dismiss="modal"
                                                            aria-label="Close"
                                                            onClick={() => setSelectedStudent(null)}></button>
                                                </div>
                                                <div className="modal-footer">
                                                    <button type="button" className="btn btn-secondary"
                                                            data-bs-dismiss="modal"
                                                            onClick={() => setSelectedStudent(null)}>Close
                                                    </button>
                                                    <button type="button" className="btn btn-danger"
                                                            onClick={() => handleDelete(item.id)}>Delete
                                                    </button>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                )}
                            </td>
                        </tr>
                    )
                }
                </tbody>
            </table>
        </>
    )
}

export default StudentListFunc;
